public class BinaryNumbers 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		System.out.println("Decimal    Binary    Hexidecimal");
		System.out.println("1               1              1");
		System.out.println("2              10              2");
		System.out.println("3              11              3");
		System.out.println("4             100              4");
		System.out.println("5             101              5");
		System.out.println("6             110              6");
		System.out.println("7             111              7");
		System.out.println("8            1000              8");
		System.out.println("9            1001              9");
		System.out.println("10           1010              A");
		System.out.println("11           1011              B");
		System.out.println("12           1100              C");
		System.out.println("13           1101              D");
		System.out.println("14           1110              E");
		System.out.println("15           1111              F");
		System.out.println("16      0001 0000             10");
		System.out.println("17      0001 0001             11");
		System.out.println("18      0001 0010             12");
		System.out.println("19      0001 0011             13");
		System.out.println("20      0001 0100             14");
	}
}

