public class TicTacToe 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		System.out.println("      |       |      ");
		System.out.println("      |       |      ");
		System.out.println("      |       |      ");
		System.out.println("---------------------");
		System.out.println("      |       |      ");
		System.out.println("      |   X   |      ");
		System.out.println("      |       |      ");
		System.out.println("---------------------");
		System.out.println("      |       |      ");
		System.out.println("      |       |      ");
		System.out.println("      |       |      ");
		System.out.println("\n");
		System.out.println("Tic Tac Toe Rules.\n");
		System.out.println("Tic Tac Toe is a game for 2 players.\n");
		System.out.println("1. A grid of 9 squares is drawn.\n");
		System.out.println("2. The first player draws an X in an empty square.\n");
		System.out.println("3. The second player drwas an O in an empty space.\n");
		System.out.println("4. Steps 2 and 3 are repeated until a Tic Tac Toe pattern is created:");
		System.out.println("   Patterns can be diagonal, horizontal, or vertical.\n");
		System.out.println("5. The winning pattern is completed by drawing a line through the pattern.");
	}
}

